# Import required libraries\nimport requests\n\n# Define a function to check website status\ndef is_website_online(url):\n    try:\n        requests.head(url)\n        return True\n    except requests.ConnectionError:\n        return False\n\n# Check if google.com is online\nif is_website_online('http://google.com'):\n    print('Online')\n\n# Run the script\nif __name__ == '__main__':\n    is_website_online('http://google.com')\n